def GetLSProData(page_data,Cookie_Jar,m,url = ''):
    from resources.lib.modules import client,cache,workers 
    import re,base64
    u = client.request('[makelist.param1]')
    u = re.findall('nofollow" href=".+?ScN0s\/([^"]*)".+?>([^<]*)', u)
    checks = ['Uptobox','Clicknupload','Openload','NitroFlare','UploadRocket']; e =[]
    for i in u:
        if any(f for f in checks if f in i[1]): e.append((base64.b64decode(i[0]),i[1]))
    return e
